mapper.select:
SELECT ar.alert_record_no, ms.station_name as measurement_station_name, dm.station_code as measurement_area, ac.alert_level as alert_level, dm.measurement_time FROM alert_record ar INNER JOIN dust_measurement dm ON ar.dust_measurement_no = dm.dust_measurement_no INNER JOIN alert_criteria ac ON ar.alert_criteria_no = ac.alert_criteria_no INNER JOIN measurement_station ms ON dm.station_code = ms.station_code
AND ms.station_name = #{station_name}
AND ac.grade = #{grade}
ORDER BY measurement_time, alert_level, measurement_station_name;

SELECT ir.inspection_record_no, ms.station_name as measurement_station_name, dm.PM10, dm.PM2_5, dm.measurement_time FROM inspection_record ir INNER JOIN dust_measurement dm ON ir.dust_measurement_no = dm.dust_measurement_no INNER JOIN measurement_station ms ON dm.station_code = ms.station_code
AND ms.station_name = #{station_name}
ORDER BY measurement_time, measurement_station_name;
